import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-game-result',
  templateUrl: './game-result.component.html',
  styleUrls: ['./game-result.component.scss']
})
export class GameResultComponent {
  @Input() isFirstPlayerTheWinner:boolean = false;
  @Output() restartButtonPressed = new EventEmitter<void>();
  public restartGame(){
    this.restartButtonPressed.emit();
  }
}
<div class="result-restart-match">
  <p class="result-restart-match-text" *ngIf="isFirstPlayerTheWinner; else firstPlayerLose">YOU WIN</p>
  <ng-template #firstPlayerLose>
    <p class="result-restart-match-text">YOU LOSE</p>
  </ng-template>
  <button (click)="restartGame()">PLAY AGAIN</button>
</div>
