import { Component, ViewChild } from '@angular/core';
import { HeaderComponent } from './components/header/header.component';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  @ViewChild(HeaderComponent) headerComponent!:HeaderComponent;
  public title = 'rock-paper-scissors';
  public optionSelectedByFirstPlayer = '';
  public optionSelectedBySecondPlayer = '';
  public isFirstPlayerTheWinner = false;
  public hasToShowModal = false;
  public isOriginalGame = true;
  public isOptionGameModeSelected = false;
  constructor() {}
  public selectedOption(option:string){
    this.optionSelectedByFirstPlayer = option;
  }
  public increaseMatchScore(isFirstPlayerTheWinner:boolean){
    this.isFirstPlayerTheWinner = isFirstPlayerTheWinner;
    if (this.isFirstPlayerTheWinner) {
      this.headerComponent.incrementMatchScore();
    }
  }
  public restartGame(){
  this.optionSelectedByFirstPlayer = '';
  this.optionSelectedBySecondPlayer = '';
  this.isFirstPlayerTheWinner = false;
  }
  public changeModalFalse(){
    this.hasToShowModal = false;
  }
  public showModal(){
    this.hasToShowModal = true;
  }
}




    <app-modal (hasToCloseModal)="changeModalFalse()" [ngClass]="{'d-none': !hasToShowModal}"></app-modal>
    <div class="menu-container" >
      <app-header></app-header>
      <div class="optionGameModeView" *ngIf="!isOptionGameModeSelected; else OptionGameModeSelected">
        <button>Orginal Game</button>
        <button>Additional Game</button>
      </div>
      <ng-template #OptionGameModeSelected>
      <app-game-options (fisrtPlayerSelection)="selectedOption($event)" *ngIf="optionSelectedByFirstPlayer === '';else optionIsSelected "></app-game-options>
      <ng-template #optionIsSelected>
        <div class="options-selected-container">
          <app-game-selections [firstPlayerSelection]="optionSelectedByFirstPlayer" [secondPlayerSelection]="optionSelectedBySecondPlayer" (isFirstPlayerTheWinner)="increaseMatchScore($event)"></app-game-selections>
          <app-game-result [isFirstPlayerTheWinner]="isFirstPlayerTheWinner" (restartButtonPressed)="restartGame()"></app-game-result>
        </div>
      </ng-template>
      <div class="button-rule">
        <button (click)="showModal()">RULES</button>
      </div>
    </ng-template>
    </div>
