import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-game-options',
  templateUrl: './game-options.component.html',
  styleUrls: ['../../app.component.scss']
})
export class GameOptionsComponent {
  @Output() fisrtPlayerSelection = new EventEmitter<string>();
  @Input() isBonusgame:boolean = false;
  public selectedOption(option:string){
    this.fisrtPlayerSelection.emit(option);
  }
}
<div class="options-container" >
  <button  class="icon-option-container btn-icons paper-icon" (click)="selectedOption('paper-icon')"></button>
  <button  class="icon-option-container btn-icons scissors-icon" (click)="selectedOption('scissors-icon')"></button>
  <button  class="icon-option-container btn-icons rock-icon" (click)="selectedOption('rock-icon')"></button>
  <button *ngIf="!isOriginalGame"  class="icon-option-container btn-icons lizard-icon" (click)="selectedOption('lizard-icon')"></button>
  <button *ngIf="!isOriginalGame" class="icon-option-container btn-icons spock-icon" (click)="selectedOption('spock-icon')"></button>
</div>
