    <div class="menu-container">
      <div class="header-container">
        <img src="../assets/images/logo.svg" alt="logo" width="75" height="55">
        <div class="score-container">
          <p>SCORE</p>
          <p>12</p>
        </div>
      </div>
      <div class="options-container" *ngIf="optionSelectedByFirstPlayer === '' ">
        <button id="paper" class="icon-option-container paper-icon" (click)="selectedOption('paper-icon')"></button>
        <button id="scissors" class="icon-option-container scissors-icon" (click)="selectedOption('scissors-icon')"></button>
        <button id="rock" class="icon-option-container rock-icon" (click)="selectedOption('rock-icon')"></button>
      </div>
      <div class="options-selected-container " *ngIf="!optionSelectedByFirstPlayer">
        <div class="first-player-selection">
          <div [ngClass]="optionSelectedByFirstPlayer"  class="icon-option-container"></div>
          <p class="icon-option-text">YOU PICKED</p>
        </div>
        <div class="second-player-selection">
          <div class="icon-option-container empty-option" *ngIf="optionSelectedBySecondPlayer === ''"></div>
          <div [ngClass]="optionSelectedBySecondPlayer"  class="icon-option-container" *ngIf="!optionSelectedBySecondPlayer"></div>
          <P class="icon-option-text">THE HOUSE PICKED</P>
        </div>
        <div class="result-restart-match">
          <p class="result-restart-match-text" *ngIf="isFirstPlayerTheWinner; else elseBlock">YOU WIN</p>
          <ng-template #elseBlock>
            <p class="result-restart-match-text">YOU LOSE</p>
          </ng-template>
          <button (click)="restartGame()">PLAY AGAIN</button>
        </div>
      </div>
      <div class="button-rule">
        <button>RULES</button>
      </div>
    </div>
