import { Component } from '@angular/core';
type GameOption = 'scissors-icon' | 'paper-icon' | 'rock-icon';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'rock-paper-scissors';
  isOptionSelected = false;
  optionSelectedByFirstPlayer = '';
  optionSelectedBySecondPlayer = '';
  matchFinished = false;
  isFirstPlayerTheWinner = false;
  hasToShowModal = false;
  matchScore = 0;
  constructor() {}
  public selectedOption(option:string){
    console.log(option);
    this.isOptionSelected = true;
    this.optionSelectedByFirstPlayer = option;
    let aleatorio = Math.floor(Math.random() * 3);
    let icon = 'paper-icon';
    switch (aleatorio) {
      case 0:
        icon = 'paper-icon';
        break;
      case 1:
        icon = 'scissors-icon';

        break;
      case 2:
        icon = 'rock-icon';
        break;
    }
    this.optionSelectedBySecondPlayer=icon;
    if (this.optionSelectedByFirstPlayer === 'scissors-icon' &&  this.optionSelectedBySecondPlayer === 'paper-icon' || this.optionSelectedByFirstPlayer === 'paper-icon' &&  this.optionSelectedBySecondPlayer === 'rock-icon' || this.optionSelectedByFirstPlayer === 'rock-icon' &&  this.optionSelectedBySecondPlayer === 'scissors-icon') {
      this.isFirstPlayerTheWinner = true;
    }
    this.checkIfFirstPlayerWin();
  }
  public checkIfFirstPlayerWin(){
    const winMap: { [key in string]: string } = {
      'scissors-icon': 'paper-icon',
      'paper-icon': 'rock-icon',
      'rock-icon': 'scissors-icon'
    };
    this.isFirstPlayerTheWinner = winMap[this.optionSelectedByFirstPlayer] === this.optionSelectedBySecondPlayer;
    this.modifyPlayerScore();
  }
  private modifyPlayerScore(){
    if(this.isFirstPlayerTheWinner)  this.matchScore++;
  }
  public restartGame(){
  this.isOptionSelected = false;
  this.optionSelectedByFirstPlayer = '';
  this.optionSelectedBySecondPlayer = '';
  this.matchFinished = false;
  this.isFirstPlayerTheWinner = false;
  }
  public changeModalFalse(){
    this.hasToShowModal = false;
  }
  public showModal(){
    this.hasToShowModal = true;
  }
}


    <!-- <div class="modal-container-wrapper" [ngClass]="{'d-none': !hasToShowModal}"> -->
      <app-modal (hasToCloseModal)="changeModalFalse()" [ngClass]="{'d-none': !hasToShowModal}"></app-modal>

    <!-- Step 1 -->
    <div class="menu-container" >
      <app-header [matchScore]="matchScore"></app-header>
      <div class="options-container" *ngIf="optionSelectedByFirstPlayer === '';else optionIsSelected " >
        <button id="paper" class="icon-option-container btn-icons paper-icon" (click)="selectedOption('paper-icon')"></button>
        <button id="scissors" class="icon-option-container btn-icons scissors-icon" (click)="selectedOption('scissors-icon')"></button>
        <button id="rock" class="icon-option-container btn-icons rock-icon" (click)="selectedOption('rock-icon')"></button>
      </div>
      <ng-template #optionIsSelected>
        <div class="options-selected-container">
          <div class="first-player-selection">
            <div [ngClass]="optionSelectedByFirstPlayer"  class="icon-option-container"></div>
            <p class="icon-option-text">YOU PICKED</p>
          </div>
          <div class="second-player-selection">
            <div class="icon-option-container empty-option" *ngIf="optionSelectedBySecondPlayer === ''"></div>
            <div [ngClass]="optionSelectedBySecondPlayer"  class="icon-option-container" *ngIf="optionSelectedBySecondPlayer !== ''"></div>
            <P class="icon-option-text">THE HOUSE PICKED</P>
          </div>
          <div class="result-restart-match">
            <p class="result-restart-match-text" *ngIf="isFirstPlayerTheWinner; else firstPlayerLose">YOU WIN</p>
            <ng-template #firstPlayerLose>
              <p class="result-restart-match-text">YOU LOSE</p>
            </ng-template>
            <button (click)="restartGame()">PLAY AGAIN</button>
          </div>
        </div>
      </ng-template>
      <div class="button-rule">
        <button (click)="showModal()">RULES</button>
      </div>
    </div>
