import { Component, EventEmitter, Input, OnChanges, Output, SimpleChanges } from '@angular/core';

@Component({
  selector: 'app-game-selections',
  templateUrl: './game-selections.component.html',
  styleUrls: ['../../app.component.scss']
})
export class GameSelectionsComponent implements OnChanges {
  @Input() firstPlayerSelection:string = '';
  @Input() secondPlayerSelection:string = '';
  @Output() isFirstPlayerTheWinner = new EventEmitter<boolean>();
  public winMap: { [key in string]: string } = {
    'scissors-icon': 'paper-icon',
    'paper-icon': 'rock-icon',
    'rock-icon': 'scissors-icon'
  };
  ngOnChanges(changes: SimpleChanges): void {
    if (changes.firstPlayerSelection.currentValue !== '' ) {
      this.secondSelectionByIA();
    }
  }
  private secondSelectionByIA(){
  const randomIconSelection = Object.keys(this.winMap)[Math.floor(Math.random() * 3)];
  this.secondPlayerSelection = this.winMap[randomIconSelection];
  this.checkIfFirstPlayerWin();
  }
  public checkIfFirstPlayerWin(){
  this.isFirstPlayerTheWinner.emit(this.winMap[this.firstPlayerSelection] === this.secondPlayerSelection);
  }
}
<div class="first-player-selection">
  <div [ngClass]="firstPlayerSelection"  class="icon-option-container"></div>
  <p class="icon-option-text">YOU PICKED</p>
</div>
<div class="second-player-selection">
  <div class="icon-option-container empty-option" *ngIf="secondPlayerSelection === ''"></div>
  <div [ngClass]="secondPlayerSelection"  class="icon-option-container" *ngIf="secondPlayerSelection !== ''"></div>
  <P class="icon-option-text">THE HOUSE PICKED</P>
</div>
